<?phpclass Language{    public $data = array();    private $default = 'pt-BR';    private $directory;    public function __construct($directory = '')    {        $this->directory = $directory;    }    public function get($key)    {        return (isset($this->data[$key]) ? $this->data[$key] : $key);    }    public function all()    {        return $this->data;    }    public function load($filename)    {        $_ = array();        $file = DIR_LANGUAGE . "/" . $this->default . '/' . $filename . '.php';        if (is_readable($file)) {            require_once($file);        }        $file = DIR_LANGUAGE . "/" . $this->directory . '/' . $filename . '.php';        if (is_readable($file)) {            require_once($file);        }        $this->data = array_merge($this->data, $_);        return $this->data;    }}