<?phpclass Player{    public function __construct($registry)    {        $this->cache = $registry->get('cache');        $this->config = $registry->get('config');    }    public function getPlayerByName($username, $timeout = 5)    {        $player_data = $this->cache->get('player.' . $username);        if (empty($player_data)) {            //cUrl > init            $uuid = curl_init("https://api.mojang.com/users/profiles/minecraft/" . $username);            //cUrl > set            curl_setopt($uuid, CURLOPT_RETURNTRANSFER, true);            curl_setopt($uuid, CURLOPT_SSL_VERIFYPEER, false);            //get cUrl            $result = curl_exec($uuid);            curl_close($uuid);            $arr_result = json_decode($result, true);            //set Cache            if (isset($arr_result['id'])) {                $this->cache->set('player.' . $username, $arr_result);            }        } else {            $arr_result = $player_data;        }        //callback        return (!empty($arr_result)) ? $arr_result : array("error" => "0");    }    public function getPlayerByUUID($uuid, $timeout = 5)    {        $player_data = $this->cache->get('player.' . $uuid);        if (empty($player_data)) {            //cUrl > init            $playername = curl_init("https://sessionserver.mojang.com/session/minecraft/profile/" . $uuid);            //cUrl > set            curl_setopt($playername, CURLOPT_RETURNTRANSFER, true);            curl_setopt($playername, CURLOPT_SSL_VERIFYPEER, false);            //get cUrl            $result = curl_exec($playername);            curl_close($playername);            $arr_result = json_decode($result, true);            //set Cache            if (isset($arr_result['id'])) {                $this->cache->set('player.' . $uuid, $arr_result);            }        } else {            $arr_result = $player_data;        }//callback        return (!empty($arr_result)) ? $arr_result : array("error" => "0");    }    public function isPremium($playername)    {        //cUrl > init        $playername = curl_init("https://minecraft.net/haspaid.jsp?user=" . $playername);        //cUrl > set        curl_setopt($playername, CURLOPT_RETURNTRANSFER, true);        curl_setopt($playername, CURLOPT_SSL_VERIFYPEER, false);        //get cUrl        $result = curl_exec($playername);        curl_close($playername);        //callback        return ($result == "true") ? true : false;    }}